javaposse.jobdsl.plugin.ExecuteDslScript.scriptText:
def jenkins_node_ip = binding.variables.get("F00")
def kvm_registry_port = binding.variables.get("F01")

freeStyleJob("ignitor") {
  label('slave')
  blockOn("base") {
    blockLevel('NODE')
    scanQueueFor('ALL')
  }
  triggers {
    genericTrigger {
      genericVariables {
        genericVariable {
          key("repo_name")
          value("\$.repository.name")
          expressionType("JSONPath")
          regexpFilter("")
          defaultValue("")
        }
      }
      printContributedVariables(true)
      printPostContent(true)
      regexpFilterText("\$repo_name")
      regexpFilterExpression("^(?!base\\b)\\b\\w+\$")
    }
  }
  steps {
    shell{
      command("""#!/bin/bash
status=`curl -I -X POST "http://${jenkins_node_ip}:${kvm_registry_port}/register-job?job_name=\${repo_name}" 2>/dev/null | head -n 1 | cut -d\$' ' -f2`
if [ -z \$status ]; then
  echo "KVM registry service is unavailable."
  exit 126
elif [ \$status -eq 412 ]; then
  echo "All KVM has been allocated, please release first."
  exit 128
elif [ \$status -ne 200 ]; then
  echo "Unexpected error occurred."
  exit 1
fi
node=\$(curl -X POST "http://${jenkins_node_ip}:${kvm_registry_port}/register-job?job_name=\${repo_name}")
curl "http://${jenkins_node_ip}:${kvm_registry_port}/trigger-script?name=register.sh&arg=\${repo_name}"
""")
    }
  }
}
